document.addEventListener('DOMContentLoaded',function(){const navbarToggle=document.querySelector('.navbar-toggle');const navbarNav=document.querySelector('.navbar-nav');if(navbarToggle&&navbarNav){navbarToggle.addEventListener('click',function(){navbarNav.classList.toggle('active');})}const navLinks=document.querySelectorAll('.navbar-nav a');navLinks.forEach(link=>{link.addEventListener('click',function(){if(navbarNav.classList.contains('active')){navbarNav.classList.remove('active')}})});const accordionItems=document.querySelectorAll('.accordion-item');accordionItems.forEach(item=>{const header=item.querySelector('.accordion-header');if(header){header.addEventListener('click',function(){accordionItems.forEach(otherItem=>{if(otherItem!==item&&otherItem.classList.contains('active')){otherItem.classList.remove('active')}});item.classList.toggle('active')})}});const lazyImages=document.querySelectorAll('.lazy-load');if('IntersectionObserver'in window){const imageObserver=new IntersectionObserver((entries,observer)=>{entries.forEach(entry=>{if(entry.isIntersecting){const img=entry.target;const src=img.getAttribute('data-src');if(src){img.src=src;img.classList.add('loaded');observer.unobserve(img)}}})});lazyImages.forEach(img=>{imageObserver.observe(img)})}else{lazyImages.forEach(img=>{const src=img.getAttribute('data-src');if(src){img.src=src;img.classList.add('loaded')}})};const contactForm=document.getElementById('contactForm');if(contactForm){contactForm.addEventListener('submit',function(e){e.preventDefault();const formMessage=document.querySelector('.form-message');if(formMessage){formMessage.textContent='Mensagem enviada com sucesso! Retornaremos em breve.';formMessage.classList.add('success');formMessage.style.display='block';contactForm.reset();setTimeout(()=>{formMessage.style.display='none'},5000)}})};const fadeElements=document.querySelectorAll('.fade-in');if('IntersectionObserver'in window){const fadeObserver=new IntersectionObserver((entries,observer)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add('visible');observer.unobserve(entry.target)}})},{threshold:0.1});fadeElements.forEach(element=>{fadeObserver.observe(element)})}else{fadeElements.forEach(element=>{element.classList.add('visible')})};document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener('click',function(e){e.preventDefault();const targetId=this.getAttribute('href');if(targetId==='#')return;const targetElement=document.querySelector(targetId);if(targetElement){window.scrollTo({top:targetElement.offsetTop-80,behavior:'smooth'})}})})});
